mapper.select:
select

from reviews
where workId = #{workId} and parentId = #{parentId}

select

from reviews
where workId = #{workId}
order by createTime
limit #{offset},#{limit}

select id
from reviews
where workId = #{workId} and parentId = #{parentId}

select reviews.id
from reviews left outer join works on reviews.workId = works.id
where reviews.id in

    #{id}

and
works.userId = #{userId}

mapper.delete:
update reviews
set subCommentCount=subCommentCount-1
where workId = #{workId} and id = #{id}

mapper.insert:
insert into reviews (id, worksId, parentId, 
  fromUserId, toUserId, subCommentCount, 
  createTime, updateTime, content
  )
values (#{id,jdbcType=INTEGER}, #{worksid,jdbcType=INTEGER}, #{parentid,jdbcType=INTEGER}, 
  #{fromuserid,jdbcType=INTEGER}, #{touserid,jdbcType=INTEGER}, #{subcommentcount,jdbcType=INTEGER}, 
  #{createtime,jdbcType=TIMESTAMP}, #{updatetime,jdbcType=TIMESTAMP}, #{content,jdbcType=LONGVARCHAR}
  )

update reviews
set subCommentCount=subCommentCount+1
where workId = #{workId} and id = #{id}
