mapper.select:
select

from reviews
where workId = #{workId} and parentId = #{parentId}

select

from reviews
where workId = #{workId}
order by createTime
limit #{offset},#{limit}

select id
from reviews
where workId = #{workId} and parentId = #{parentId}

select reviews.id
from reviews left outer join works on reviews.workId = works.id
where reviews.id in

    #{id}

and
works.userId = #{userId}

select count(DISTINCT id)
from reviews
where workId = #{workId}

mapper.delete:
update reviews
set subCommentCount=subCommentCount-1
where workId = #{workId} and id = #{id}

mapper.insert:
update reviews
set subCommentCount=subCommentCount+1
where workId = #{workId} and id = #{id}
